    private void tfNombreDelProductoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tfNombreDelProductoActionPerformed
        // TODO add your handling code here:
        
        try{
            if(tfNombreDelProducto.getText().isEmpty()){
                throw new DatoObligatorio();
            }
            if(T8p3ej1.validarProducto(tfNombreDelProducto.getText()) ==false){
                throw new ProductoNoValido();
            }
            tfUnidades.setEnabled(true);
            tfUnidades.requestFocus();
        
        }
        catch(DatoObligatorio e){
            javax.swing.JOptionPane.showMessageDialog(this,"El campo del producto no puede estas vacio");
            tfNombreDelProducto.requestFocus();
        }
        catch(ProductoNoValido e){
            javax.swing.JOptionPane.showMessageDialog(this,"El producto indicado no existe");
            tfNombreDelProducto.requestFocus();
        }
        catch(Exception e){
            System.out.println("Error " + e.getMessage());
      
        }
        
    }//GEN-LAST:event_tfNombreDelProductoActionPerformed

    private void tfUnidadesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tfUnidadesActionPerformed
        // TODO add your handling code here:
        try{
            if(tfUnidades.getText().isEmpty()){
                throw new DatoObligatorio();
            }
        }
        catch(DatoObligatorio e){
            javax.swing.JOptionPane.showMessageDialog(this,"El campo del producto no puede estas vacio");
            tfNombreDelProducto.requestFocus();
        }
        catch(Exception e){
            System.out.println("Error " + e.getMessage());
      
        }
        
        
    }//GEN-LAST:event_tfUnidadesActionPerformed

